mapper.select:
select * from tms_outlets_info o_i
LEFT JOIN lc_xzqh_info x_i on o_i.county = x_i.id
where x_i.`name` =  #{name,jdbcType=VARCHAR}
and company_id = #{companyId,jdbcType=BIGINT}

  select * from tms_outlets_info
	WHERE status = 1
	and company_id = #{outletsInfo.companyId}

		and province = #{outletsInfo.province,jdbcType=VARCHAR}


		and name like concat("%",#{outletsInfo.name,jdbcType=VARCHAR},"%")

  	limit #{page.start},#{page.limit}

select count(1) from tms_outlets_info WHERE status = 1 and company_id = #{outletsInfo.companyId,jdbcType=BIGINT}
and province = #{outletsInfo.province,jdbcType=VARCHAR}
and name like concat("%",#{outletsInfo.name,jdbcType=VARCHAR},"%")

select oi.*,puc.company_name,puc.company_code,xi.name as province_value,xii.name as city_value, xiii.name as county_value,u.login_name,u.true_name,pd.name as type_value from tms_outlets_info oi left join lc_platform_user_company puc on oi.company_id = puc.id left join lc_xzqh_info xi on oi.province = xi.id left join lc_xzqh_info xii on oi.city = xii.id left join lc_xzqh_info xiii on oi.county = xiii.id left join tms_user u on oi.create_person_id = u.id left join lc_platform_dictionary pd on pd.id = oi.type
oi.company_id = #{user.companyId}
and oi.name like concat("%",#{outletsInfo.condition},"%") or oi.contact_person like concat("%",#{outletsInfo.condition},"%")
order by create_time desc limit #{page.start},#{page.limit}

select count(1) from tms_outlets_info oi left join lc_platform_user_company puc on oi.company_id = puc.id left join lc_xzqh_info xi on oi.province = xi.id left join lc_xzqh_info xii on oi.city = xii.id left join lc_xzqh_info xiii on oi.county = xiii.id left join tms_user u on oi.create_person_id = u.id left join lc_platform_dictionary pd on pd.id = oi.type
oi.company_id = #{user.companyId}
and oi.name like concat("%",#{outletsInfo.condition},"%") or oi.contact_person like concat("%",#{outletsInfo.condition},"%")

select oi.*,puc.company_name,xi.name as province_value,xii.name as city_value, xiii.name as county_value,pd.name as type_value from tms_outlets_info oi left join lc_platform_user_company puc on oi.company_id = puc.id left join lc_xzqh_info xi on oi.province = xi.id left join lc_xzqh_info xii on oi.city = xii.id left join lc_xzqh_info xiii on oi.county = xiii.id left join lc_platform_dictionary pd on pd.id = oi.type WHERE 1=1
and oi.name = #{searchParams.condition} or puc.company_name = #{searchParams.condition}
and oi.province = #{searchParams.startProvince}
and oi.city = #{searchParams.startCity}
and oi.county = #{searchParams.startCounty}
limit #{page.start},#{page.limit}

select count(1) from tms_outlets_info oi left join lc_platform_user_company puc on oi.company_id = puc.id left join lc_xzqh_info xi on oi.province = xi.id left join lc_xzqh_info xii on oi.city = xii.id left join lc_xzqh_info xiii on oi.county = xiii.id left join lc_platform_dictionary pd on pd.id = oi.type WHERE 1=1
and oi.name = #{searchParams.condition} or puc.company_name = #{searchParams.condition}
and oi.province = #{searchParams.startProvince}
and oi.city = #{searchParams.startCity}
and oi.county = #{searchParams.startCounty}

mapper.update:
update
	tms_outlets_info
set
	status = #{status}
where
	id = #{outletsId}

mapper.select:
select t.*, pb.*,pb.id pos_bind_id from (SELECT oi.*, com.company_name, com.company_code FROM tms_outlets_info oi LEFT JOIN lc_platform_user_company com ON oi.company_id = com.id where 1 = 1
and oi.company_id = #{companyId,jdbcType=BIGINT}
and oi.id = #{outletsId,jdbcType=BIGINT}
order by create_time desc limit #{start}, #{limit}) t left join tms_pos_bind pb on t.id = pb.outlets_id where 1 = 1
and (pb.pos_sn like concat("%",#{condition,jdbcType=VARCHAR},"%") or pb.login_account like concat("%",#{condition,jdbcType=VARCHAR},"%"))

  	select count(1) from
  	(SELECT oi.*, com.company_name FROM tms_outlets_info oi 
	LEFT JOIN lc_platform_user_company com ON oi.company_id = com.id where 1 = 1

   		and oi.company_id = #{companyId,jdbcType=BIGINT}


   		and oi.id = #{outletsId,jdbcType=BIGINT}

   	) t
   	left join tms_pos_bind pb on t.id = pb.outlets_id where 1 = 1 

   		and (pb.pos_sn like concat("%",#{condition,jdbcType=VARCHAR},"%") 
   		or pb.login_account like concat("%",#{condition,jdbcType=VARCHAR},"%"))

  	SELECT count(1) FROM tms_outlets_info oi 
	LEFT JOIN lc_platform_user_company com ON oi.company_id = com.id where 1 = 1

   		and oi.company_id = #{companyId,jdbcType=BIGINT}


   		and oi.id = #{outletsId,jdbcType=BIGINT}
